  ${
    recn.1 $e |- A e. RR $.
    $( A real number is a complex number. $)
    recn   $p |- A e. CC $=
      ( cr wcel cc axresscn ssi ax-mp ) ACDAEDCEAFGBH $.
      $([1-Mar-95]$)
  $}

  $( 0 is a complex number. $)
  0cn $p |- 0 _10 e. CC $=
      ( cn0 ax0re recn ) ABC $.
      $([1-Mar-95]$)

  $( 1 is a complex number. $)
  1cn $p |- 1 _10 e. CC $=
