  $( A real number is a complex number. $)
  recnt $p |- ( A e. RR -> A e. CC ) $=
    ( cr cc axresscn ssi ) BCADE $.
    $([10-Aug-99]$)

  ${
    recn.1 $e |- A e. RR $.
    $( A real number is a complex number. $)
    recn   $p |- A e. CC $=
      ( cr cc axresscn ssii ) CDAEBF $.
      $([1-Mar-95]$)
  $}

  ${
    recnd.1 $e |- ( ph -> A e. RR ) $.
    $( Deduction from real number to complex number. $)
    recnd $p |- ( ph -> A e. CC ) $=
      ( cr wcel cc recnt syl ) ABDEBFECBGH $.
      $([26-Oct-99]$)
  $}

  $( 0 is a complex number. $)
  0cn $p |- 0_10 e. CC $=
    ( cn0 ax0re recn ) ABC $.
    $([1-Mar-95]$)

  $( 1 is a complex number. $)
  1cn $p |- 1_10 e. CC $=
    ( cn1 ax1re recn ) ABC $.
    $([1-Mar-95]$)

