    $([25-Apr-94]$)

  $( Exportation theorem expressed with primitive connectives. $)
  expt  $p |- ( ( -. ( ph -> -. ps ) -> ch ) -> ( ph -> ( ps -> ch ) ) ) $=
    ( wn wi pm3.2im syl4d comm ) AABDEDZCEBCEABICABFGH $.
    $([5-Aug-93]$)

  ${
