  ${
    mtbir.1 $e |- ( ph <-> ps ) $.
    mtbir.2 $e |- -. ps $.
    $( An inference from a biconditional, related to modus tollens. $)
    mtbir     $p |- -. ph $=
      ( wn bineg mpbir ) AEBEABCFDG $.
      $([15-Nov-94]$)
  $}

  ${
