    mpbiri.min $e |- ch $.
    $( An inference from a nested biconditional, related to modus ponens. $)
    mpbiri     $p |- ( ph -> ps ) $=
      ( biimprd mpi ) ACBABCDFEG $.
      $([5-Aug-93]$)
  $}

  ${
    mpbid.maj $e |- ( ph -> ( ps <-> ch ) ) $.
    mpbid.min $e |- ( ph -> ps ) $.
    $( A deduction from a biconditional, related to modus ponens. $)
    mpbid     $p |- ( ph -> ch ) $=
