  $c ~_q $. $( Equivalence relation to construct positive fractions $)
  $c N_q $. $( Set of positive fractions $)
  $c 1_q $. $( Positive fraction constant ` 1 ` $)
  $c +_q $. $( Positive fraction addition $)
  $c ._q $. $( Positive fraction multiplication $)
  $c *_q $. $( Positive fraction reciprocal $)
  $c <_q $. $( Positive fraction ordering relation $)

  $c N_p $. $( Set of positive reals $)
