  $($[ set.mm $]$)

  $( Axioms for a Hilbert space $)
  $( We assume we've constructed a fixed constant class ` Hil ` and
     associated operations, and we postulate their properties. $)

  $c Hil $.
  $c +v $.
  $c .s $.
  $c 0v $.
  $c -v $.
  $c .i $.
  $c norm $.
  $c Cauchy $.
  $c ~~> $.
  $c Subspace $.
  $c ComplSubsp $.
  $c ClosedSubsp $.
  $c SH $.
  $c CH $.
  $c _|_ $.
  $c +H $.
  $c ^H $.
  $c vH $.
  $c =<H $.
  $c 0H $.
  $c 1H $.

  $( Extend class notation with Hilbert vector space. $)
  chil $a class Hil $.
  $( Extend class notation with vector addition on Hilbert space. $)
  cva $a class +v $.
  $( Extend class notation with scalar multiplication on Hilbert space. $)
  csm $a class .s $.
  $( Extend class notation with zero vector in Hilbert space. $)
  c0v $a class 0v $.
  $( Extend class notation with vector subtraction in Hilbert space. $)
  cmv $a class -v $.
  $( Extend class notation with inner (scalar) product in Hilbert space. $)
  csp $a class .i $.
  $( Extend class notation with norm function in Hilbert space. $)
  csno $a class norm $.
  $( Extend class notation with Cauchy sequence relation in Hilbert space. $)
  ccau $a class  Cauchy $.
  $( Extend class notation with convergence relation in Hilbert space. $)
  cli $a class  ~~> $.
  $( Extend class notation with subspace relation in Hilbert space. $)
  cssp $a class  Subspace $.
  $( Extend class notation with complete subspace relation in Hilbert space. $)
  ccms $a class  ComplSubsp $.
  $( Extend class notation with closed subspace relation in Hilbert space. $)
  ccss $a class  ClosedSubsp $.
  $( Extend class notation with set of subspaces of a Hilbert space. $)
  csh $a class  SH $.
  $( Extend class notation with set of closed subspaces of a Hilbert space. $)
  cch $a class  CH $.
  $( Extend class notation with orthogonal complement in ` CH ` . $)
  cort $a class  _|_ $.
  $( Extend class notation with subspace sum in ` CH ` . $)
  cph $a class  +H $.
  $( Extend class notation with meet in ` CH ` . $)
  cjh $a class  ^H $.
  $( Extend class notation with join in ` CH ` . $)
  cmh $a class  vH $.
  $( Extend class notation with partial ordering in ` CH ` . $)
  cleh $a class  =<H $.
  $( Extend class notation with zero of ` CH ` . $)
  c0h $a class  0H $.
  $( Extend class notation with unit of ` CH ` . $)
  c1h $a class  1H $.

  $( Postulates for vector space. $)
  ax-hilex $a |- Hil e. V $.

  $( Closure of vector addition. $)
  ax-hvaddcl $a |- ( ( A e. Hil /\ B e. Hil ) -> ( A +v B ) e. Hil ) $.

  ${
    hvaddcl.1 $e |- A e. Hil $.
    hvaddcl.2 $e |- B e. Hil $.
    $( Closure inference for vector addition. $)
    hvaddcl $p |- ( A +v B ) e. Hil $=
      ( chil wcel cva co ax-hvaddcl mp2an ) AEFBEFABGHEFABICDJ $.
      $([1-Aug-99]$)
  $}

  $( Vector addition is commutative. $)
  ax-hvcom $a |- ( ( A e. Hil /\ B e. Hil ) -> ( A +v B ) = ( B +v A ) ) $.

  $( Vector addition is associative. $)
  ax-hvass $a |- ( ( A e. Hil /\ B e. Hil /\ C e. Hil ) ->
                 ( ( A +v B ) +v C ) = ( A +v ( B +v C ) ) ) $.

  $( The zero vector is in the vector space. $)
  ax-hvzercl $a |- 0v e. Hil $.

  $( Addition with the zero vector. $)
  ax-hvaddid $a |- ( A e. Hil -> ( A +v 0v ) = A ) $.

  $( Closure of scalar multiplication. $)
  ax-hvmulcl $a |- ( ( A e. CC /\ B e. Hil ) -> ( A .s B ) e. Hil ) $.

  ${
    hvmulcl.1 $e |- A e. CC $.
    hvmulcl.2 $e |- B e. Hil $.
    $( Closure inference for scalar multiplication. $)
    hvmulcl $p |- ( A .s B ) e. Hil $=
      ( cc wcel chil csm co ax-hvmulcl mp2an ) AEFBGFABHIGFABJCDK $.
      $([1-Aug-99]$)
  $}

  $( Scalar multiplication by one. $)
  ax-hvmulid $a |- ( A e. Hil -> ( 1 _10 .s A ) = A ) $.

  $( Scalar multiplication associative law $)
  ax-hvmulass $a |- ( ( A e. CC /\ B e. CC /\ C e. Hil ) ->
                 ( ( A x. B ) .s C ) = ( A .s ( B .s C ) ) ) $.

  $( Scalar multiplication distributive law $)
  ax-hvdistr1 $a |- ( ( A e. CC /\ B e. Hil /\ C e. Hil ) ->
                ( A .s ( B +v C ) ) = ( ( A .s B ) +v ( A .s C ) ) ) $.

  $( Scalar multiplication distributive law $)
  ax-hvdistr2 $a |- ( ( A e. CC /\ B e. CC /\ C e. Hil ) ->
                ( ( A + B ) .s C ) = ( ( A .s C ) +v ( B .s C ) ) ) $.

  $( Scalar multiplication by zero.  We can derive the negative
     of a vector from this. $)
  ax-hvmulzer $a |- ( A e. Hil -> ( 0 _10 .s A ) = 0v ) $.

  $( Define vector subtraction. $)
  ax-hdfvsub $a |- ( ( A e. Hil /\ B e. Hil ) ->
     ( A -v B ) = ( A +v ( -u 1 _10 .s B ) ) ) $.

  ${
    hvsubcl.1 $e |- A e. Hil $.
    hvsubcl.2 $e |- B e. Hil $.
    $( Closure inference for vector subtraction. $)
    hvsubcl $p |- ( A -v B ) e. Hil $=
      ( cmv co chil wcel cn1 cneg csm cva wceq ax-hdfvsub mp2an eleq1i 1cn
      negcl hvmulcl hvaddcl mpbir ) ABEFZGHAIJZBKFZLFZGHUBUEGAGHBGHUBUEMABNCDOP
      AUDCUCBIQRDSTUA $.
      $([2-Aug-99]$)
  $}

  hvsubclt $p |- ( ( A e. Hil /\ B e. Hil ) -> ( A -v B ) e. Hil ) $=
    ( chil wcel cmv co c0v cded wceq opreq1 eleq1d opreq2 ax-hvzercl elimel
    hvsubcl dedth2h ) ACDZBCDZABEFZCDQAGHZBEFZCDTRBGHZEFZCDABGGATISUACATBEJKBUB
    IUAUCCBUBTELKTUBAGCMNBGCMNOP $.
    $([17-Aug-99]$)

  $( Scalar multiplication with the zero vector. $)
  hmul0t $p |- ( A e. CC -> ( A .s 0v ) = 0v ) $=
    ( cc wcel c0v cn0 csm co cmulc mulzer1t opreq1d chil wceq ax-hvzercl
    ax-hvmulzer ax-mp syl6eq ax-hvmulass 0cn mp3an2 mpan2 eqtr3d opreq2i
    syl6req ) ABCZDAEDFGZFGZADFGUDAEHGZDFGZDUFUDUHUEDUDUGEDFAIJDKCZUEDLMDNOZPUD
    UIUHUFLZUDEBCUIUKAEDQRSMTUAUEDAFUJUBUC $.
    $([30-May-99]$)

  $( Subtraction of a vector from itself. $)
  hsubidt $p |- ( A e. Hil -> ( A -v A ) = 0v ) $=
    ( chil wcel cmv co cn0 csm c0v cn1 cneg caddc cva ax-hvmulid opreq1d cc
    wceq ax-hvdistr2 1cn mp3an1 negcl mpan ax-hdfvsub anabss 3eqtr4rd negid
    opreq1i syl6eq ax-hvmulzer eqtrd ) ABCZAADEZFAGEZHUJUKIIJZKEZAGEZULUJIAGEZU
    MAGEZLEZAUQLEZUOUKUJUPAUQLAMNUMOCZUJUOURPZIOCUTUJVAIUMAQRSIRTUAUJUKUSPAAUBU
    CUDUNFAGIRUEUFUGAUHUI $.
    $([30-May-99]$)

  ${
    haddid2.1 $e |- A e. Hil $.
    $( Addition with the zero vector. $)
    haddid2 $p |- ( 0v +v A ) = A $=
      ( c0v cva co chil wcel wceq ax-hvcom ax-hvzercl mp2an ax-hvaddid ax-mp
      eqtr ) CADEZACDEZACFGAFGZOPHCAIJBKQPAHBALMN $.
      $([18-Aug-99]$)

    $( Addition of negative of a vector to itself. $)
    hnegid $p |- ( A +v ( -u 1 _10 .s A ) ) = 0v $=
      ( cmv co cn1 cneg csm cva c0v chil wcel wceq ax-hdfvsub mp2an hsubidt
      ax-mp eqtr3 ) AACDZAEFAGDHDZIAJKZTRSLAAMBBNTRILBAOPQ $.
      $([18-Aug-99]$)
  $}


  ${
    hadd23.1 $e |- A e. Hil $.
    hadd23.2 $e |- B e. Hil $.
    hadd23.3 $e |- C e. Hil $.
    $( Hilbert vector space ommutative/associative law. $)
    hadd23 $p |- ( ( A +v B ) +v C ) = ( ( A +v C ) +v B ) $=
      ( cva co chil wcel wceq ax-hvcom mp2an opreq2i ax-hvass mp3an 3eqtr4 ) AB
      CGHZGHZACBGHZGHZABGHCGHZACGHBGHZRTAGBIJZCIJZRTKBCLEFMNAIJZUDUEUBSKABCODEF
      PUFUEUDUCUAKACBODFEPQ $.
      $([18-Aug-99]$)
  $}

  ${
    hsubeq0.1 $e |- A e. Hil $.
